for un_texte in textes:
	cursor.execute("""
	INSERT INTO users(name, titre, oeuvre_date, texte) VALUES(:name, :titre, :oeuvre_date, :texte)""", un_texte)


cursor.execute("""SELECT id, name, titre, oeuvre_date, texte FROM users""")
rows = cursor.fetchall()
for row in rows:
    print('{0} : {1} - {2} - {3} - {4}'.format(row[0], row[1], row[2],row[3], row[4]))


